def countdown(number):
    print(number)
    if number > 0:
        countdown(number)

countdown(5)

def harmonic_sum(number):
    if number < 1:
        return 0
    else:
        return 1 / number

print(harmonic_sum(7))

def print_with_o_upper(txt, index=0):
    if index == len(txt):
        return
    else:
        if txt[index] == 'o':
            print(txt[index].upper(), end='')
        else:
            print(txt[index], end='')

txt = "Look, good code is almost like a good song"
print_with_o_upper(txt)
